/* TABLES */

CREATE TABLE stg_flink_user_ml (
	message_date TIMESTAMP(3),
      message_id VARCHAR,
      message_in_reply_to VARCHAR,
      message_from_name VARCHAR,
      message_from_email VARCHAR,
      message_subject VARCHAR,
      PRIMARY KEY(message_id)
);

ALTER TABLE stg_flink_user_ml REPLICA IDENTITY FULL;

CREATE TABLE flink_user_ml_topics (
      message_id VARCHAR,
      message_date TIMESTAMP(3),
      message_from_name VARCHAR,
      topic VARCHAR(2),
      topic_description VARCHAR(50),
      PRIMARY KEY(message_id)
);

CREATE TABLE dim_topics (
      id VARCHAR(2),
      topic VARCHAR(50),
      topic_weigh VARCHAR(100),
      PRIMARY KEY(id)
);


